    private ArrayList<Word> sentence = new ArrayList<>();
    private String wordToType;
    private ArrayList<Character> enteredWord = new ArrayList<>();
    private ArrayList<Integer> failedWords = new ArrayList<>();
    private int wordsFailed = 0;
    private String failingWord;
    private ArrayList<Integer> correctWords = new ArrayList<>();
    private int letterCount;
    private int wordCount;
    private int changeCount;
    private Timer timer = new Timer();
    private boolean started = false;
    private int secondsLeft = 60;

    public GamePlay(LayoutManager layout) {
        super(layout);
        addKeyListener(this);
        setFocusable(true);
        setFocusTraversalKeysEnabled(false);
        addToSentence();
        this.letterCount = 0;
        this.wordCount = 0;
        this.changeCount = 0;
    }

    public void paint(Graphics g){
        g.setColor(Color.WHITE);
        g.fillRect(1,1, 800, 592);
        g.setColor(Color.YELLOW);
        g.fillRect(0,0,3,592);
        g.fillRect(0,0,800,3);
        g.fillRect(792, 0, 3, 592);
        g.setColor(Color.BLACK);
        g.setFont(new Font("serif", Font.BOLD, 20));
        if(!started){
            g.drawString("Your WPM is: " + Integer.toString(letterCount/5-wordsFailed),20 ,200);
        }
        g.drawString(Integer.toString(secondsLeft), 330, 250);
        int wordXAxis = 10;
        int wordYAxis = 300;
        for(int i = 0; i < sentence.size(); i++){
            g.setColor(Color.BLACK);
           if(wordXAxis + g.getFontMetrics().stringWidth(sentence.get(i).getWord()) + 8 > 800){
               wordXAxis = 10;
               wordYAxis += 50;
               changeCount = i;
           }
            if(i == wordCount){
                g.setColor(Color.LIGHT_GRAY);
                Rectangle r = new Rectangle(wordXAxis-2, wordYAxis-20, g.getFontMetrics().stringWidth(sentence.get(i).getWord())+2, 30);
                g.fillRect(r.x, r.y, r.width, r.height);
                g.setColor(Color.BLACK);
            }
           if(sentence.get(i).getWord().equals(failingWord) && wordCount == i){
               g.setColor(Color.LIGHT_GRAY);
               Rectangle r = new Rectangle(wordXAxis-2, wordYAxis-20, g.getFontMetrics().stringWidth(sentence.get(i).getWord())+2, 30);
               g.fillRect(r.x, r.y, r.width, r.height);
               g.setColor(Color.RED);
           }
           for(Integer failedIndex : failedWords){
               if(i == failedIndex){
                   g.setColor(Color.RED);
               }
           }
           for(Integer correctIndex : correctWords){
               if(i == correctIndex){
                   g.setColor(Color.GREEN);
               }
           }
            g.drawString(sentence.get(i).getWord(), wordXAxis, wordYAxis);
           wordXAxis += g.getFontMetrics().stringWidth(sentence.get(i).getWord()) + 8;
        }
        g.setColor(Color.BLACK);
        g.drawString(enteredWord.stream().map(String::valueOf).collect(Collectors.joining()), 10, 450);
        g.dispose();
    }

    private void addToSentence(){
        if(changeCount != 0){
            failedWords.clear();
            correctWords.clear();
            wordCount = 0;
            for(int i = 0; i < changeCount; i++){
                sentence.remove(0);
                sentence.add(new Word(words[(int)(Math.random()*words.length)]));
            }
        }
        else {
            for(int i = 0; i < 20; i++){
                sentence.add(new Word(words[(int)(Math.random()*words.length)]));
            }
            this.wordToType = sentence.get(wordCount).getWord();
        }
    }

    private void startTimer(){
        this.timer.schedule(new TimerTask() {
            @Override
            public void run() {
                if(secondsLeft > 0){
                    secondsLeft--;
                }
                if(secondsLeft == 0){
                    started = false;
                }
                repaint();
            }
        }, 0, 1000);
    }


    @Override
    public void actionPerformed(ActionEvent e) {
    }

    @Override
    public void keyTyped(KeyEvent e) {

    }

    @Override
    public void keyPressed(KeyEvent e) {
        boolean correct = false;
        if(!started && e.getKeyCode() == KeyEvent.VK_ENTER){
            startTimer();
            started = true;
        }
        if(started){
            if(Character.isLetter(e.getKeyChar())){
                enteredWord.add(e.getKeyChar());
                letterCount ++;
            }
            if(e.getKeyCode() == KeyEvent.VK_BACK_SPACE && enteredWord.size() > 0){
                enteredWord.remove(enteredWord.size()-1);
            }
            String finalEnteredWord = enteredWord.stream().map(String::valueOf).collect(Collectors.joining());
            if(finalEnteredWord.equals(wordToType)){
                correct = true;
            }
            if(finalEnteredWord.length() > wordToType.length() && finalEnteredWord.length() > 0){
                if(finalEnteredWord.equals(wordToType)){
                    failingWord = "";
                }
                else {
                    failingWord = wordToType;
                }
            }
            if(finalEnteredWord.length() <= wordToType.length() && finalEnteredWord.length() > 0){
                if(finalEnteredWord.equals(wordToType.substring(0, enteredWord.size()))){
                    failingWord = "";
                }
                else {
                    failingWord = wordToType;
                }
            }
            if(e.getKeyCode() == KeyEvent.VK_SPACE) {
                if(correct){
                    correctWords.add(wordCount);
                    enteredWord.clear();
                    wordCount ++;
                    score ++;
                    wordToType = sentence.get(wordCount).getWord();
                }
                else {
                    failedWords.add(wordCount);
                    enteredWord.clear();
                    wordCount ++;
                    wordToType = sentence.get(wordCount).getWord();
                    wordsFailed ++;
                }
            }
            if(changeCount == wordCount){
                addToSentence();
            }
        }
        repaint();
    }

    @Override
    public void keyReleased(KeyEvent e) {

    }
